# function to reformat model terms

get_terms &lt;- function(mod,add_fixed_var=T){
  fe_tbl &lt;- as.data.frame(glmmTMB::fixef(mod)$cond)
  colnames(fe_tbl)&lt;-"coef"
  fe_tbl$se &lt;- diag(glmmTMB::sandwich(mod))

  add_var &lt;- fe_tbl["(Intercept)","se"]
  if (!add_fixed_var) add_var &lt;- 0

  re_tbl &lt;- as.data.frame(glmmTMB::ranef(mod, condVar = T)) %&gt;% 
    filter(component == "cond") %&gt;% 
    mutate(val=condval,
           sd=condsd)

  comp_sel &lt;- unique(re_tbl$grpvar)
  u_comp_sel &lt;- str_split(comp_sel,"\\:")
  u_comp_sel &lt;- unique(unlist(u_comp_sel))
  u_comp_sel &lt;- unique(gsub("`","",u_comp_sel))

  for (i in u_comp_sel) {
    comp_sel_sub &lt;- comp_sel[sapply(comp_sel,function(x) any(grepl(i,x)))]
    re_tbl[[i]]&lt;-NA_character_

    for (ii in comp_sel_sub) {
      pos &lt;- str_split(ii,"\\:",simplify = T)
      pos &lt;- gsub("`","",pos)
      pos &lt;- which(pos==i)

      re_tbl[re_tbl$grpvar==ii,i] &lt;- str_split(re_tbl[re_tbl$grpvar==ii,"grp"],"\\:",simplify= T)[,pos]
    }
  }

  for (i in unique(re_tbl$SiteCode[!is.na(re_tbl$SiteCode)])){

    for (ii in unique(re_tbl$term)){
      val_sum &lt;- re_tbl$val[re_tbl$SiteCode %in% i & re_tbl$term %in% ii]
      sd_sum &lt;- re_tbl$sd[re_tbl$SiteCode %in% i & re_tbl$term %in% ii]

      for (iii in unique(re_tbl$grpvar)[!grepl("SiteCode",unique(re_tbl$grpvar))]){
        val_sum &lt;- c(val_sum, re_tbl$condval[re_tbl$grpvar %in% iii &
                                               re_tbl$grp %in% gsub(paste0(":",i),"",unique(re_tbl[["grp"]][re_tbl$SiteCode %in% i])) &
                                               re_tbl$term %in% ii
        ]
        )
        sd_sum &lt;- c(sd_sum, re_tbl$condsd[re_tbl$grpvar %in% iii &
                                            re_tbl$grp %in% gsub(paste0(":",i),"",unique(re_tbl[["grp"]][re_tbl$SiteCode %in% i])) &
                                            re_tbl$term %in% ii
        ]
        )
      }

      re_tbl$val[re_tbl$SiteCode %in% i & re_tbl$term %in% ii] &lt;- sum(val_sum)
      re_tbl$sd[re_tbl$SiteCode %in% i & re_tbl$term %in% ii] &lt;- sqrt(sum(sd_sum^2)+add_var^2)

    }
  }

  comp_list &lt;- re_tbl %&gt;% 
    filter(!is.na(SiteCode))%&gt;% 
    mutate(group_var="SiteCode") %&gt;% 
    select(group_var,group=SiteCode,component,term,val,sd,any_of(u_comp_sel)) %&gt;% 
    group_by(group_var,group,component,term) %&gt;% 
    summarise(
      value = sum(val),
      sd = sqrt(sum(sd^2)+add_var^2),
      across(any_of(u_comp_sel),~unique(.x)),
      .groups = "drop"
    )

  comp_list &lt;- bind_rows(
    comp_list,
    re_tbl %&gt;% 
      filter(is.na(SiteCode)) %&gt;% 
      filter(!grpvar %in% unique(comp_list$group_var)) %&gt;% 
      select(group_var=grpvar,
             group=grp,
             component,
             term,
             val=condval,
             sd=condsd,
             any_of(u_comp_sel)) %&gt;% 
      group_by(across(c(any_of(u_comp_sel),group_var,group,component,term))) %&gt;% 
      summarise(
        value = sum(val),
        sd = sqrt(sum(sd^2)+add_var^2),
        .groups = "drop"
      )
  )

  comp_list &lt;- comp_list %&gt;% 
    mutate(
      pred_low = value - (qnorm(1 - 0.1 / 2) * sd),
      pred_high = value + (qnorm(1 - 0.1 / 2) * sd),
      pred_low_trend = value - (qnorm(1 - 0.5 / 2) * sd),
      pred_high_trend = value + (qnorm(1 - 0.5 / 2) * sd),
    ) 

  return(comp_list)

}

get_terms2 &lt;- function(mod,add_fixed_var=F,terms){

  fixed_list &lt;- list(
    "`UCA Class`:`Stress Class`:`Habitat Stability Class`" = as.formula(~`UCA Class`*`Stress Class`*`Habitat Stability Class`),
    "`UCA Class`:`Habitat Stability Class`" = as.formula(~`UCA Class`*`Habitat Stability Class`),
    "`UCA Class`:`Stress Class`" = as.formula(~`UCA Class`*`Stress Class`),
    "`Habitat Stability Class`" = as.formula(~`Habitat Stability Class`),
    "`Stress Class`" = as.formula(~`Stress Class`),
    "`UCA Class`" = as.formula(~`UCA Class`),
    "`1`" = as.formula(~1)
  )

  fixed_list &lt;- map(fixed_list,
                    ~{
                      emt95 &lt;- emmeans::emtrends(mod,.x,var="Year_n",level=0.95) %&gt;% # consider: addl.vars = "SiteCode" ,component="response"
                        data.frame(check.names = F) %&gt;% 
                        mutate(level=0.95,term="Year_n") %&gt;% 
                        rename(val=Year_n.trend)

                      emt50 &lt;- emmeans::emtrends(mod,.x,var="Year_n",level=0.5) %&gt;% 
                        data.frame(check.names = F) %&gt;% 
                        mutate(level=0.5,term="Year_n") %&gt;% 
                        rename(val=Year_n.trend)

                      emm95 &lt;- emmeans::emmeans(mod,.x,level=0.95, at = list(Year_n = max(mod$frame$Year_n)),type="response") %&gt;% 
                        data.frame(check.names = F) %&gt;% 
                        mutate(level=0.95,term="(Intercept)") %&gt;% 
                        rename(val=response)
                      emm50 &lt;- emmeans::emmeans(mod,.x,level=0.50, at = list(Year_n = max(mod$frame$Year_n)),type="response") %&gt;% 
                        data.frame(check.names = F) %&gt;% 
                        mutate(level=0.5,term="(Intercept)") %&gt;% 
                        rename(val=response)

                      bind_rows(emm95,emm50,emt95,emt50) %&gt;% 
                        rowwise() %&gt;% 
                        mutate(grp=paste0(c_across(any_of(c("UCA Class","Stress Class","Habitat Stability Class"))),collapse = ":"))
                    })

  fixed_list &lt;- bind_rows(fixed_list,.id="grpvar") %&gt;% 
    mutate(component="fixed") 

  re_tbl &lt;- as.data.frame(glmmTMB::ranef(mod, condVar = T)) %&gt;% 
    filter(component == "cond") %&gt;% 
    filter(grpvar == "`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode") %&gt;% 
    mutate(SiteCode = str_split(grp,":",simplify = T)[,4]) %&gt;% 
    rename(val=condval,
           SE=condsd)

  out_frame_raw &lt;- distinct(mod$frame[,sapply(mod$frame,class)=="factor"]) %&gt;% 
    mutate(val = NA_real_,
           SE = NA_real_)

  out_frame &lt;- out_frame_raw

  for (i in unique(out_frame$SiteCode)) {
    sub_frame1 &lt;- fixed_list %&gt;% 
      filter(term == "Year_n") %&gt;% 
      filter(level == 0.95) %&gt;% 
      filter(
        `UCA Class` == out_frame[out_frame$SiteCode==i,"UCA Class"],
        `Habitat Stability Class` == out_frame[out_frame$SiteCode==i,"Habitat Stability Class"],
        `Stress Class` == out_frame[out_frame$SiteCode==i,"Stress Class"]
      )

    sub_frame2 &lt;- re_tbl %&gt;% 
      filter(SiteCode==i) %&gt;% 
      filter(term == "Year_n")

    out_frame$val[out_frame$SiteCode==i] &lt;- sum(sub_frame1$val,na.rm = T) + sum(sub_frame2$val,na.rm = T)
    out_frame$SE[out_frame$SiteCode==i] &lt;- sqrt(sum(sub_frame1$SE^2,na.rm = T) + sum(sub_frame2$SE^2,na.rm = T))

  }

  out_frame &lt;- out_frame %&gt;% 
    mutate(
      pred_low = val - (qnorm(1 - 0.1 / 2) * SE),
      pred_high = val + (qnorm(1 - 0.1 / 2) * SE),
      pred_low_trend = val - (qnorm(1 - 0.5 / 2) * SE),
      pred_high_trend = val + (qnorm(1 - 0.5 / 2) * SE),
    ) %&gt;% 
    mutate(
      term = "Year_n",
      component = "cond",
      grpvar = "`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode",
    )

  fixed_list_wide &lt;- fixed_list %&gt;% 
    pivot_wider(names_from = level, values_from = c(asymp.LCL,asymp.UCL)) %&gt;% 
    rename(
      pred_low = asymp.LCL_0.95,
      pred_low_trend = asymp.LCL_0.5,
      pred_high = asymp.UCL_0.95,
      pred_high_trend = asymp.UCL_0.5
    )



  return(
    bind_rows(out_frame,fixed_list_wide) %&gt;% 
      rename(value=val,
             group = grp,
             group_var = grpvar,
             sd = SE)
  )

}

summary_fun &lt;- function(x){
  out &lt;- map(
    c("Subshed","WSHED","UCA Class","Stress Class","UCA Class:Stress Class","UCA Class:Stress Class","UCA Class:Habitat Stability Class","UCA Class:Stress Class:Habitat Stability Class"),
    ~{x %&gt;% 
        as_tibble() %&gt;% 
        mutate(`UCA Class:Stress Class` = paste(`UCA Class`,`Stress Class`,sep = ".")) %&gt;% 
        mutate(`UCA Class:Habitat Stability Class` = paste(`UCA Class`,`Habitat Stability Class`,sep = ".")) %&gt;% 
        mutate(`UCA Class:Stress Class:Habitat Stability Class` = paste(`UCA Class`,`Stress Class`,`Habitat Stability Class`,sep = ".")) %&gt;% 
        mutate(group=.x) %&gt;% 
        rename_with(.cols=any_of(.x),~paste0("group_val")) %&gt;% 
        mutate(group_val=as.character(group_val)) %&gt;% 
        group_by(group,group_val) %&gt;% 
        summarise(
          across(c(
            `Upstream Catchment Area`,
          ),
          ~mean(log10(.x),na.rm=T)^10
          ),
          across(c(
            `Reach Slope`,
          ),
          ~expm1(mean(log1p(.x),na.rm=T))
          ),
          across(c(
            `Baseflow Index`,
            `Upstream catchment developed (%)`,
            `Upstream catchment agricultural (%)`,
            `Upstream catchment stress index`
          ),
          ~median(.x,na.rm=T)
          ),
          .groups="drop"
        )
    }
  )

  out &lt;- bind_rows(out)
  return(out)
}

# Function to extract model optim

glmmTMB_get_optimum &lt;- function(mod) {
  # Fixed effects
  starting_point &lt;- list(
    beta = glmmTMB::fixef(mod)$cond,
    betazi = glmmTMB::fixef(mod)$zi,
    betadisp = glmmTMB::fixef(mod)$disp
  )
  #Random effects
  ref &lt;- glmmTMB::ranef(mod)
  if (length(ref$cond) &gt; 0) {
    starting_point$theta = ref$cond
  }
  if (length(ref$zi) &gt; 0) {
    starting_point$thetazi = ref$zi
  }
  if (length(ref$disp) &gt; 0) {
    starting_point$thetadisp = ref$disp
  }
  return(starting_point)
}

# Function to generate heatmaps
heatmap_fn&lt;-function(x_df,
                     y_df=NULL,
                     alpha=0.1,
                     sig_est=0.6,
                     method="spearman",
                     scale_grad=ggplot2::scale_fill_gradient2(
                       low = scales::muted(rgb(red=0/255,green=102/255,blue=255/255)),
                       mid = "white",
                       high = scales::muted(rgb(red=0/255,green=176/255,blue=80/255)),
                       limits=c(-1,1)
                     ),
                     ...){

  if (is.null(y_df)){
    y_df&lt;-x_df
  }
  cor_list&lt;-lapply(x_df,function(x) lapply(y_df,function(y) broom::tidy(cor.test(x,y,method=method,...))))

  pcorr_out&lt;-purrr::map_dfr(cor_list,.id="x_var",~purrr::map_dfr(.,dplyr::bind_rows,.id="y_var"))

  pcorr_out &lt;- pcorr_out %&gt;%
    mutate(p.value = case_when(
      estimate &gt;= 1 - .Machine$double.eps ~ NA,
      estimate &lt;= -1 + .Machine$double.eps~ NA,
      T ~ p.value
    )) %&gt;%
    mutate(estimate = case_when(
      estimate &gt;= 1 - .Machine$double.eps ~ NA,
      estimate &lt;= -1 + .Machine$double.eps~ NA,
      T ~ estimate
    )) 

  rho&lt;-pcorr_out %&gt;%
    select(x_var,y_var,estimate) %&gt;%
    spread(x_var,estimate) %&gt;%
    data.frame(check.names = F) %&gt;%
    tibble::column_to_rownames("y_var") %&gt;%
    textshape::cluster_matrix(method ="ward.D2")

  pv&lt;-pcorr_out %&gt;%
    mutate(p.value=case_when(
      is.na(p.value) ~ "darkgray",
      p.value&gt;alpha ~ "darkgray",
      p.value&lt;=alpha & abs(estimate)&gt;=sig_est ~ "darkred",
      p.value&lt;=alpha ~ "orange",
      T ~ "darkgray"
    )) %&gt;%
    select(x_var,y_var,p.value) %&gt;%
    spread(x_var,p.value) %&gt;%
    data.frame(check.names = F) %&gt;%
    tibble::column_to_rownames("y_var")

  pv&lt;-pv[rownames(rho),colnames(rho)]

  plot_out &lt;- pcorr_out %&gt;%
    select(x_var,y_var,estimate) %&gt;% 
    spread(x_var,estimate) %&gt;%
    data.frame(check.names = F) %&gt;%
    tibble::column_to_rownames("y_var") %&gt;%
    heatmaply::ggheatmap(hclust_method ="ward.D2",
                         seriate="GW",
                         margins =c(40,40,40,40),
                         branches_lwd = 0.25,
                         grid_gap=3,
                         heatmap_layers=list(
                           theme(
                             axis.text.y = element_text(size = 8)   ,
                             axis.text.x = element_text(
                               size = 8,
                               hjust = 1,
                               vjust = 1,
                               angle = 45
                             ),
                             legend.position = "none"),
                           geom_tile(data=pv %&gt;%
                                       rownames_to_column("Endpoint") %&gt;%
                                       gather("Parameter","Value",-Endpoint),
                                     aes(x=Parameter,y=Endpoint),
                                     #alpha=0,
                                     colour=pv %&gt;%
                                       rownames_to_column("Endpoint") %&gt;%
                                       gather("Parameter","Value",-Endpoint) %&gt;%
                                       pull(Value),
                                     width=0.975, height=0.975,
                                     size=0.3,
                                     fill=NA,
                                     inherit.aes = F),
                           guides(colour="none")
                         ),
                         scale_fill_gradient_fun = scale_grad)

  cowplot::ggdraw(plot_out)

}

# transformer for car::logit
clogit &lt;- scales::new_transform(
  name="clogit",
  transform = function(x) {
    x[x&lt;0] &lt;- 0
    x[x&gt;1] &lt;- 1
    car::logit(x,T,0.025)
  },
  inverse = function(x) {
    a &lt;- 0.025
    a &lt;- (1-2*a)
    a &lt;- (a*(1+exp(x))+(exp(x)-1))/(2*a*(1+exp(x)))
    zapsmall(a)
  },
  domain = c(0, 1)
)

# Functions to plot axis on pseudo-log scales
force_all &lt;- function(...) list(...) # Helper function defined in scales

pseudo_log &lt;- function (val, sigma = 1, base = exp(1)) {
  asinh(val/(2*sigma))/log(base)
}

pseudo_log_breaks &lt;- function(n = 5, sigma = 1, base = 10) {
  force_all(n, base)
  n_default = n

  force_all &lt;- function(...) list(...) # Helper function defined in scales

  pseudo_log &lt;- function (val, sigma = 1, base = exp(1)) {
    asinh(val/(2*sigma))/log(base)
  }

  function(x, n = n_default) {
    rng &lt;- pseudo_log(range(x, na.rm = TRUE), sigma = sigma, base = base)
    min &lt;- floor(rng[1])
    max &lt;- ceiling(rng[2])

    if (max == min) return(base^min)

    by &lt;- floor((max - min) / n) + 1
    breaks &lt;- base^seq(min, max, by = by)
    relevant_breaks &lt;- base^rng[1] &lt;= breaks & breaks &lt;= base^rng[2]
    if (sum(relevant_breaks) &gt;= (n - 2)) return(breaks)

    # the easy solution to get more breaks is to decrease 'by'
    while (by &gt; 1) {
      by &lt;- by - 1
      breaks &lt;- base^seq(min, max, by = by)
      relevant_breaks &lt;- base^rng[1] &lt;= breaks & breaks &lt;= base^rng[2]
      if (sum(relevant_breaks) &gt;= (n - 2)) return(breaks)
    }
    scales:::log_sub_breaks(rng, n = n, base = base)
  }
}

# Helper function to extract a legend
get_legend &lt;- function(p, position = NULL){

  if(.is_list(p)){
    continue &lt;- TRUE
    i &lt;- 1
    while(i &lt;= length(p) & continue){
      leg &lt;- .get_legend(p[[i]], position = position)
      if(!is.null(leg)) continue &lt;- FALSE
      i &lt;- i+1
    }
  }
  else{
    leg &lt;- .get_legend(p, position = position)
  }
  leg
}

.is_list &lt;- function(x){
  inherits(x, "list")
}


# Return legend for one plot
.get_legend &lt;- function(p, position = NULL){

  if(is.null(p)) return(NULL)
  if(!is.null(position)){
    p &lt;- p + theme(legend.position = position)
  }
  tmp &lt;- ggplot_gtable(ggplot_build(p))
  leg &lt;- which(sapply(tmp$grobs, function(x) x$name) == "guide-box")
  if(length(leg) &gt; 0) leg &lt;- tmp$grobs[[leg]]
  else leg &lt;- NULL
  leg
}

col2hex &lt;- function(color, alpha = 1) {

  if (alpha &lt; 0 | alpha &gt; 1)
    stop('alpha must be between 0 and 1.')

  if (missing(color))
    stop('Need a color')

  if (!is.character(color))
    stop(strwrap('Elements must be character string as a named R color or
         hex (e.g. "#ffffff")'))

  if (!color %in% colors()) {
    if (!nchar(color) == 7 | !grepl('^#', color))
      stop(strwrap('color must be an R color, e.g. see colors(),
                   or a hex of the form #ff5500'))
  }

  rgb(t(col2rgb(color)/255), alpha = alpha)
}

# Calculate fish endpoints
calc_fish_ep&lt;-function(df) {

  ep_tbl&lt;-df %&gt;% 
    group_by(SampleEventID,SampleDate) %&gt;% 
    mutate(
      # General Community
      Comm_Biomass=sum(EstimatedBiomass,na.rm=T),
      Comm_Abundance=sum(NumberOfFish,na.rm=T),
      Comm_Biomass_SATI=sum(EstimatedBiomass[!is.na(OPT_TEMP)],na.rm=T),
      Comm_Abundance_SATI=sum(NumberOfFish[!is.na(OPT_TEMP)],na.rm=T),
      Comm_Richness=length(unique(SpeciesCode)),
      EstimatedBiomass_perc=EstimatedBiomass/Comm_Biomass,
      NumberOfFish_perc=NumberOfFish/Comm_Abundance,
      EstimatedBiomass_perc_SATI=EstimatedBiomass/Comm_Biomass_SATI,
      NumberOfFish_perc_SATI=NumberOfFish/Comm_Abundance_SATI,
      SATI_EstimatedBiomass_perc=EstimatedBiomass_perc_SATI*OPT_TEMP,
      SATI_NumberOfFish_perc=NumberOfFish_perc_SATI*OPT_TEMP,
    ) %&gt;% 
    summarise(
      # General Community
      Comm_Biomass=head(Comm_Biomass,1),
      Comm_Abundance=head(Comm_Abundance,1),
      Comm_Richness=head(Comm_Richness,1),
      # Thermal Regime 
      Therm_SATI_Bioperc=sum(SATI_EstimatedBiomass_perc,na.rm=T),
      Therm_SATI_Abuperc=sum(SATI_NumberOfFish_perc,na.rm=T),

      Therm_cold_Bioperc=sum(EstimatedBiomass_perc[Thermal.Regime=="coldwater"],na.rm=T),
      Therm_cool_Bioperc=sum(EstimatedBiomass_perc[Thermal.Regime=="coolwater"],na.rm=T),
