      Therm_cool_Bio=sum(EstimatedBiomass_perc[Thermal.Regime=="coolwater"],na.rm=T)*Comm_Biomass,
      Therm_coolcold_Bio=sum(EstimatedBiomass_perc[Thermal.Regime %in% c("coolwater","coldwater")],na.rm=T)*Comm_Biomass,
      Therm_warm_Bio=sum(EstimatedBiomass_perc[Thermal.Regime %in% c("warmwater")],na.rm=T)*Comm_Biomass,
      Therm_cold_Abu=sum(NumberOfFish_perc[Thermal.Regime=="coldwater"],na.rm=T)*Comm_Abundance,
      Therm_cool_Abu=sum(NumberOfFish_perc[Thermal.Regime=="coolwater"],na.rm=T)*Comm_Abundance,
      Therm_coolcold_Abu=sum(NumberOfFish_perc[Thermal.Regime %in% c("coolwater","coldwater")],na.rm=T)*Comm_Abundance,
      Therm_warm_Abu=sum(NumberOfFish_perc[Thermal.Regime %in% c("warmwater")],na.rm=T)*Comm_Abundance,
