source("00_source.R")
library(openxlsx)


# Read in Data ------------------------------------------------------------
dat &lt;- read_csv("data/tblChannelStructureSummary.csv", locale = locale(encoding = "latin1"))


# Add additional endpoints ------------------------------------------------
dat &lt;- dat %&gt;% 
  mutate(
    TotalCoverTypePCWood = EmbCoverTypePCWood + UnembCoverTypePCWood,
    TotalCoverTypePCRndRock = EmbCoverTypePCRndRock + UnembCoverTypePCRndRock,
    TotalCoverTypePCFltRock = EmbCoverTypePCFltRock + UnembCoverTypePCFltRock,
    TotalCoverTypePCMacrophyte = EmbCoverTypePCMacrophyte + UnembCoverTypePCMacrophyte,
    TotalCoverTypePCBank = EmbCoverTypePCBank + UnembCoverTypePCBank,
  ) %&gt;% 
  mutate(
    TotalVegTypePCFL = EmbVegTypePCFL + UnembVegTypePCFL,
    TotalVegTypePCAL = EmbVegTypePCAL + UnembVegTypePCAL,
    TotalVegTypePCSS = EmbVegTypePCSS + UnembVegTypePCSS,
    TotalVegTypePCMC = EmbVegTypePCMC + UnembVegTypePCMC,
  )

# Exclude test data
dat &lt;- dat %&gt;% 
  select(all_of(c(unlist(ID_cols),unlist(met_cols),StreamType = "substrateQualityRatingCode"))) %&gt;% 
  filter(!StreamCode %in% c("TEST")) %&gt;% 
  filter(!grepl("Training",OrgNameList)) %&gt;% 
  filter(SiteCode != "NCD21-1C") #%&gt;% # TRCA site not within bounds of TRCA shape file
  #filter(SiteCode != "PT003WM") %&gt;%  # incorrect coordinates
  #filter(SiteCode != "PT002WM") 
# filter(month(SampleDate) != "1",
#        month(SampleDate) != "2",
#        month(SampleDate) != "12")

# Remove samples without location information
dat &lt;- dat  %&gt;% 
  rename_with(~gsub("^.*?\\.","",.x)) %&gt;% 
  mutate(Longitude = as.numeric(Longitude),
         Latitude = as.numeric(Latitude)) %&gt;% 
  group_by(SiteCode) %&gt;% 
  mutate(Longitude = median(Longitude,na.rm = T),
         Latitude = median(Latitude,na.rm = T),
  ) %&gt;% 
  filter(!is.na(Longitude),
         !is.na(Latitude)) %&gt;% 
  ungroup()

# Add utility columns
dat &lt;- dat %&gt;% 
  group_by(!!!syms(ID_cols[!ID_cols %in% c("ID","SampleDate","Sample","Longitude","Latitude")]))  %&gt;% 
  mutate(
    Month = month(SampleDate),
    Year = year(SampleDate),
    `Sample Season` = case_when(
      Month %in% 3:5 ~ "Spring",
      Month %in% 6:8 ~ "Summer",
      Month %in% 9:11 ~ "Fall",
      T ~ "Winter"
    ),
    `Sample Years` = paste(sort(unique(Year)),collapse = ", "),
    `Sample Months` = paste(sort(unique(Month)),collapse = ", "),
    `Years of Data` = length(unique(Year)),
    `Year-Month`=format(SampleDate,"%Y-%m"),
    `Sample Year-Months` = paste(sort(unique(`Year-Month`)),collapse = ", "),
    `Sample Year-Season` = paste(sort(unique(`Year-Month`)),collapse = ", "),
    `TRCA` = OrgNameList == "Toronto and Region Conservation Authority",
    `Sample Season`=factor(`Sample Season`,levels=c("Sping","Summer","Fall","Winter"))
  ) %&gt;% 
  filter(Year &lt; 2025) %&gt;% 
  ungroup() %&gt;% 
  group_by(!!!syms(c(ID_cols[!ID_cols %in% c("ID","SampleDate","Sample","Longitude","Latitude")],"Year"))) %&gt;% 
  mutate( 
    `Sampled Days within Year` = length(unique(SampleDate)),
    `Sampled Seasons within Year` = length(unique(`Sample Season`)),
  ) %&gt;% 
  ungroup() %&gt;% 
  rowwise() %&gt;% 
  mutate(
    `Number of Missing Endpoints` = sum(is.na(c_across(unlist(sapply(met_cols,names),use.names = F))))
  ) %&gt;% 
  ungroup() %&gt;% 
  mutate(
    Year_n = (max(Year)-min(Year))-(max(Year)-Year)+1,
    year_class = case_when(
      Year %in% 1995:2000 ~ "1995-2000",
      Year %in% 2001:2006 ~ "2001-2006",
      Year %in% 2007:2012 ~ "2007-2012",
      Year %in% 2013:2018 ~ "2013-2018",
      Year %in% 2019:2024 ~ "2019-2024",
    )
  ) %&gt;% 
  mutate(
    year_class = factor(year_class,levels=rev(c("1995-2000","2001-2006","2007-2012","2013-2018","2019-2024")))
  )


# Duplicate TRCA data in full analysis subset -----------------------------
dat &lt;- bind_rows(
  dat %&gt;% mutate(TRCA=F),
  dat %&gt;% filter(TRCA) %&gt;% mutate(TRCA=T)
)


# Filter Data -------------------------------------------------------------
dat &lt;- dat %&gt;% 
  filter(`Years of Data` &gt; 2 & length(unique(year_class)) &gt; 1) %&gt;% 
  group_by(SiteCode,TRCA) %&gt;% 
  filter(
    !TRCA | `Years of Data` &gt; 3,
    !TRCA | any(Year &gt; 2018),
    !TRCA | length(unique(year_class)) &gt; 1
  ) %&gt;% 
  ungroup() 



if (F){
  # Get Ranges -------------------------------------------------------------

  dat %&gt;% 
    pivot_longer(all_of(unlist(sapply(met_cols,names),use.names = F)),
                 names_to = "Endpoint") %&gt;% 
    ggplot(aes(x=value)) +
    geom_histogram() +
    facet_wrap(~Endpoint,scales="free")

  dat %&gt;% 
    pivot_longer(all_of(unlist(sapply(met_cols,names),use.names = F)),
                 names_to = "Endpoint") %&gt;% 
    group_by(Endpoint) %&gt;% 
    summarise(
      min = min(value,na.rm = T),
      max = max(value,na.rm = T)
    ) %&gt;% 
    print(n=Inf)

}

# Convert Percent to Proportion -------------------------------------------

dat &lt;- dat %&gt;% 
  mutate(
    across(
      contains(c("Cover","Percent")),
      ~ .x/100
    )
  )

# Replace problematic values ------------------------------------------------


dat &lt;- dat %&gt;% 
  mutate(
    `Habitat Stability Score` = `Habitat Stability Score` - 0.5,
    `Habitat Homogeneity` = 1-`Habitat Homogeneity`,
    across(
      c(D15,D50,D85,D50Max),
      ~ case_when(
        .x &lt; 0 ~ NA_real_,
        .x &lt;= 2 ~ 0,
        .x &gt;= 1000 ~ NA_real_,
        T ~ .x
      )
    ),
    D15 = case_when(
      D15 &gt; 50 ~ NA_real_,
      T ~ D15
    ),
    across(
      c(`Sediment Transport Potential`),
      ~ case_when(
        .x &lt; 0 ~ NA_real_,
        T ~ .x
      )
    ),
    across(
      c(`Sediment Sorting Index`),
      ~ case_when(
        .x &lt; 0 ~ NA_real_,
        T ~ .x
      )
    ),
    across(
      c(
        contains(c("Cover","Percent")),
        `Habitat Stability Score`,
        `Habitat Homogeneity`,
        `Bank Stability - Mean`
      ),
      ~ case_when(
        .x &gt;= 1 ~ quantile(.x[.x&lt;1 & .x&gt;0],0.99,na.rm=T),
        .x &lt; 0 ~ NA_real_,
        T ~ .x
      )
    ),
    across(
      c(
        contains(c("Cover","Percent"))
      ),
      ~ case_when(
        .x &lt; 0.02 ~ 0,
        T ~ .x
      )
    )
  )


# Convert to sf, and attribute with watershed info

dat &lt;- dat %&gt;% 
  sf::st_as_sf(coords=c("Longitude","Latitude"),crs="WGS84",remove = F)


# Geospatial analysis -----------------------------------------------------

