  ggplot(dat_in,aes(x=value)) +
    geom_histogram() +
    scale_x_continuous(trans="log1p")+
    facet_wrap(~Endpoint,scales="free")
}

# Setup Model param -------------------------------------------------------

dat_in &lt;- dat_in %&gt;% 
  filter(`Stress Class` != "North") %&gt;% # Exclude sites without land cover data
  filter(Endpoint != c("Habitat Stability Score")) %&gt;%  # Has a problematic distribution
  filter(Endpoint != c("Sediment Transport Potential")) %&gt;%  # Has a problematic distribution
  group_by(TRCA) %&gt;% 
  mutate(
    Year_n2 = Year,
  ) %&gt;% 
  mutate(
    Year_n = (max(Year)-min(Year))-(max(Year)-Year)+1,
  ) %&gt;% 
  ungroup() %&gt;% 
  mutate(
    `Strahler Order` = as.factor(`Strahler Order`)
  )

ep_tbl &lt;- enframe(met_cols) %&gt;% 
  unnest(value) %&gt;% 
  mutate(Endpoint = names(value)) %&gt;% 
  unnest(value)

mod_data_fin &lt;- dat_in %&gt;% 
  as_tibble() %&gt;% 
  group_by(across(any_of(c("TRCA","Family","Endpoint")))) %&gt;% 
  nest() %&gt;% 
  ungroup() %&gt;% 
  left_join(enframe(met_cols) %&gt;% 
              unnest(value) %&gt;% 
              mutate(Endpoint = names(value)) %&gt;% 
              unnest(value) %&gt;% 
              select(-value,Endpoint_group=name,Endpoint),
            by = "Endpoint" ) %&gt;% 
  mutate(Endpoint = factor(Endpoint,levels = ep_tbl$Endpoint),
         Endpoint_group = factor(Endpoint_group,levels = unique(ep_tbl$name))) %&gt;% 
  mutate(model_params = case_when(
    Endpoint %in% c("D15",
                    "D50",
                    "D85",
                    "D50Max"
    ) ~ list(
      list(
        family = lognormal(link = "log"),
        ziformula = ~ 1,
        dispformula = ~ 1
      )
    ),
    Endpoint %in% c("Sediment Sorting Index"
    ) ~ list(
      list(
        family = ziGamma(link = "log"),
        ziformula = ~ 1,
        dispformula = ~ 1
      )
    ),
    Endpoint %in% c("Width:Depth Ratio",
                    "Width - Mean",
                    "Depth - Mean") ~ list(
                      list(
                        family = lognormal(link = "log"),
                        ziformula = ~ 0,
                        dispformula = ~ 1
                      )
                    ),
    Endpoint %in% c("Sediment Transport Potential") ~ list(
      list(
        family = Gamma(link = "sqrt"),
        ziformula = ~ 0,
        dispformula = ~ 1
      )
    ),
    Endpoint %in% c("Usable Area") ~ list(
      list(
        family = Gamma(link = "log"),
        ziformula = ~ 0,
        dispformula = ~ 1
      )
    ),
    grepl(" Cover| Percent",Endpoint) | Endpoint %in% c("Habitat Homogeneity",
                                                        "Habitat Stability Score")  ~ list(
                                                          list(
                                                            family = beta_family(link = "logit"),
                                                            ziformula = ~ 1,
                                                            dispformula = ~ 1
                                                          )
                                                        ),
    Endpoint %in% c("Bank Stability - Mean")  ~ list(
      list(
        family = beta_family(link = "probit"),
        ziformula = ~ 0,
        dispformula = ~ 1
      )
    ),
    Endpoint %in% c("") ~ list(
      list(
        family = gaussian(link = "identity"),
        ziformula = ~ 1,
        dispformula = ~ 1
      )
    )
  )) %&gt;% 
  mutate(model_params = map2(TRCA,model_params,
                             ~case_when(.x==T ~ c(.y,
                                                  list(
                                                    control = glmmTMBControl(parallel = parallel::detectCores()),
                                                    formula = as.formula("value ~ `Upstream catchment developed (%)` + `Upstream catchment agricultural (%)` + log1p(`Reach Slope`) + Year_n + `UCA Class` + `Habitat Stability Class` + `Stress Class` + Year_n:`UCA Class` + Year_n:`Habitat Stability Class` + Year_n:`Stress Class` + Year_n:`UCA Class`:`Stress Class` + Year_n:`UCA Class`:`Habitat Stability Class` + (Year_n|`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode)")
                                                  )),
                                        .x==F ~ c(.y,
                                                  list(
                                                    control = glmmTMBControl(parallel = parallel::detectCores()),
                                                    formula = as.formula("value ~ `Upstream catchment developed (%)` + `Upstream catchment agricultural (%)` + log1p(`Reach Slope`) + Year_n + `UCA Class` + `Habitat Stability Class` + `Stress Class` + Year_n:`UCA Class` + Year_n:`Habitat Stability Class` + Year_n:`Stress Class` + Year_n:`UCA Class`:`Stress Class` + Year_n:`UCA Class`:`Habitat Stability Class` + (Year_n|`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode)")
                                                  ))
                             )

  )) %&gt;% 
  mutate(model_params = map2(TRCA,model_params,
                             ~{
                               if (.y$dispformula[[2]]!=0 & .x==T){
                                 .y$dispformula &lt;- as.formula(" ~ 1")
                               }
                               if (.y$dispformula[[2]]!=0 & .x==F){
                                 .y$dispformula &lt;- as.formula(" ~ 1")
                               }
                               if (.y$ziformula[[2]]!=0 & .x==T){
                                 .y$ziformula &lt;- as.formula(" ~ `Upstream catchment developed (%)` + `Upstream catchment agricultural (%)` + log1p(`Reach Slope`) + Year_n + `UCA Class` + `Habitat Stability Class` + `Stress Class` + Year_n:`UCA Class` + Year_n:`Habitat Stability Class` + Year_n:`Stress Class` + Year_n:`UCA Class`:`Stress Class` + Year_n:`UCA Class`:`Habitat Stability Class` + (Year_n|`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode)")
                               }
                               if (.y$ziformula[[2]]!=0 & .x==F){
                                 .y$ziformula &lt;- as.formula(" ~ `Upstream catchment developed (%)` + `Upstream catchment agricultural (%)` + log1p(`Reach Slope`) + Year_n + `UCA Class` + `Habitat Stability Class` + `Stress Class` + Year_n:`UCA Class` + Year_n:`Habitat Stability Class` + Year_n:`Stress Class` + Year_n:`UCA Class`:`Stress Class` + Year_n:`UCA Class`:`Habitat Stability Class` + (Year_n|`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode)")
                               }
                               return(.y)
                             }
  )) %&gt;% 
  mutate(model_params = map2(model_params,data, 
                             ~c(.x,
                                list(
                                  data= .y %&gt;% 
                                    filter(!is.na(value)) %&gt;% 
                                    select(
                                      any_of(
                                        c("WSHED","Subshed","SiteCode","Area_class",
                                          "Sample_Season","Year_n","Year_n2","value",
                                          "year_class","Year",
                                          "Upstream Catchment Area","Baseflow Index","Reach Slope","Strahler Order",
                                          "Upstream catchment developed (%)","Upstream catchment agricultural (%)",
                                          "Upstream catchment stress index","Stress Class","Habitat Stability Class","UCA Class")
                                      )
                                    ) 
                                )
                             ))) %&gt;% 
  mutate(Endpoint = factor(Endpoint, levels = unlist(sapply(met_cols,names),use.names=F)))


mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(p0 = map_dbl(data,~sum(.x$value==0,na.rm=T)/length(.x$value==0)))

mod_data_fin &lt;- mod_data_fin %&gt;% 
  filter(p0 &lt; 0.75)

# Run Models --------------------------------------------------------------

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(
    model = map(model_params, ~do.call(glmmTMB::glmmTMB,.x))
  ) 

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(
    pred_error = map_lgl(model,
                         ~any(
                           is.na(
                             predict(.x,
                                     type="link",
                                     re.form = NULL,
                                     se.fit = TRUE,
                                     cov.fit = F,
                                     do.bias.correct = F,
                                     allow.new.levels = T)$se.fit
                           ) |
                             any(is.na(as.data.frame(glmmTMB::ranef(.x, condVar = T))$condsd))
                         ) 
    )
  )

# Exclude outliers --------------------------------------------------------
mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(
    model_params = case_when(
      pred_error == T ~ map2(model_params,model, 
                             ~{
                               .x$data &lt;- .x$data[!DHARMa::outliers(DHARMa::simulateResiduals(.y, use.u = T,refit = F,plot = F,n=9999),return = "logical",lowerQuantile = 0.025, upperQuantile = 0.975),]
                               return(.x)
                             }
      ),
      T ~ model_params
    )
  ) 

# Refit Models --------------------------------------------------------------

mod_data_fin &lt;- mod_data_fin %&gt;%
  mutate(
    model = case_when(
      pred_error == T ~ map(model_params, ~do.call(glmmTMB::glmmTMB,.x)), #refit models with default links
      T ~ model
    )
  )

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(
    pred_error = map_lgl(model,
                         ~any(
                           is.na(
                             predict(.x,
                                     type="link",
                                     re.form = NULL,
                                     se.fit = TRUE,
                                     cov.fit = F,
                                     do.bias.correct = F,
                                     allow.new.levels = T)$se.fit
                           ) |
                             any(is.na(as.data.frame(glmmTMB::ranef(.x, condVar = T))$condsd))
                         ) 
    )
  )



print(mod_data_fin,n=Inf)

# adjust link functions for bad fits
mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(model = pmap(
    list(.x=model,
         .y=pred_error,
         .z=model_params),
    function(.x,.y,.z){
      if (!.y) return(.x)
      #if (.x$call$family$family != "beta") return(.x)
      if (.x$call$family$family == "beta") {
        .z$family &lt;- beta_family(link = "probit")
        .x &lt;- do.call(glmmTMB::glmmTMB,.z)
      }
      if (.x$call$family$family == "Gamma") {
        .z$family &lt;- ziGamma(link = "sqrt")
        .x &lt;- do.call(glmmTMB::glmmTMB,.z)
      }
      if (.x$call$family$family == "lognormal") {
        .z$family &lt;- lognormal(link = "sqrt")
        .x &lt;- do.call(glmmTMB::glmmTMB,.z)
      }
      return(.x)
    })
  )

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(
    pred_error = map2_lgl(model,
                          pred_error,
                          ~{
                            if (!.y) return(.y)
                            any(
                              is.na(
                                predict(.x,
                                        type="link",
                                        re.form = NULL,
                                        se.fit = TRUE,
                                        cov.fit = F,
                                        do.bias.correct = F,
                                        allow.new.levels = T)$se.fit
                              )
                            ) |
                              any(is.na(as.data.frame(glmmTMB::ranef(.x, condVar = T))$condsd))
                          })
  )

print(mod_data_fin,n=Inf)



mod_data_fin &lt;- mod_data_fin %&gt;% 
  filter(!pred_error)

# Predict observations ----------------------------------------------------

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(Pred1 = map2(model,model_params, function (i,nm) {

    year_vals &lt;- nm$data %&gt;% 
      select(Year_n,Year_n2,year_class) %&gt;% 
      distinct()  

    pred_frame &lt;- nm$data %&gt;% 
      select(any_of(c("WSHED","Subshed","Area_class","SiteCode",
                      "Upstream Catchment Area","Baseflow Index","Reach Slope","Strahler Order",
                      "Upstream catchment developed (%)","Upstream catchment agricultural (%)",
                      "Upstream catchment stress index","Stress Class","Habitat Stability Class","UCA Class"))) %&gt;% 
      distinct() %&gt;% 
      mutate(Sample_Season="Summer",#list(factor(levels(nm$data$Sample_Season),levels = levels(nm$data$Sample_Season))),
             Year_n = list(seq(from=min(year_vals$Year_n),to=max(year_vals$Year_n)))
      ) %&gt;%
      unnest(Year_n) %&gt;%
      unnest(Sample_Season) %&gt;% 
      left_join(
        year_vals
      )

    pred_frame&lt;-pred_frame[complete.cases(pred_frame),]

    a1 &lt;- pred_frame %&gt;% 
      bind_cols(
        data.frame(
          pred=predict(i,
                       newdata = pred_frame,
                       type="link",
                       re.form = NULL,
                       se.fit = TRUE,
                       cov.fit = F,
                       do.bias.correct = F,
                       allow.new.levels = T)
        )
      ) %&gt;%
      mutate(pred.fit = predict(i,newdata = pred_frame,type="response")) %&gt;% 
      mutate(pred.fit = i$modelInfo$family$linkfun(pred.fit)) %&gt;% 
      mutate(
        pred_low = pred.fit - (qnorm(1 - 0.1 / 2) * pred.se.fit),
        pred_high = pred.fit + (qnorm(1 - 0.1 / 2) * pred.se.fit),
        pred_low_trend = pred.fit - (qnorm(1 - 0.5 / 2) * pred.se.fit),
        pred_high_trend = pred.fit + (qnorm(1 - 0.5 / 2) * pred.se.fit),
      ) %&gt;% 
      left_join(
        nm$data %&gt;% select(any_of(c("WSHED","Subshed","SiteCode","Area_class",
                                    "Sample_Season","Year_n","Year_n2",
                                    "value","year_class",
                                    "Upstream Catchment Area","Baseflow Index","Reach Slope","Strahler Order",
                                    "Upstream catchment developed (%)","Upstream catchment agricultural (%)",
                                    "Upstream catchment stress index","Stress Class","Habitat Stability Class","UCA Class")))
      ) %&gt;% 
      mutate(
        across(
          c(pred.fit,pred_low,pred_high,pred_low_trend,pred_high_trend),
          ~i$modelInfo$family$linkinv(.x)
        )
      ) 

    return(a1)
  }))

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(Pred1_error = map_lgl(Pred1,~any(is.na(.x$pred.se.fit))))

print(mod_data_fin,n=Inf)

# Extract random effects terms --------------------------------------------

mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(rand_ef = map(model,~get_terms2(.x))) 


mod_data_fin &lt;- mod_data_fin %&gt;% 
  mutate(rand_ef_error = map_lgl(rand_ef,~any(is.na(.x$sd))))

print(mod_data_fin,n=Inf)

