# Format final data tables ------------------------------------------------

mod_data_fin &lt;- mod_data_fin %&gt;%
  mutate(
    Pred2 = map(Pred1,
                ~ .x %&gt;% 
                  group_by(WSHED, Subshed, Sample_Season, SiteCode, year_class,
                           `Upstream Catchment Area`,`Baseflow Index`,`Reach Slope`,`Strahler Order`,
                           `Upstream catchment developed (%)`,`Upstream catchment agricultural (%)`,
                           `Upstream catchment stress index`,`Stress Class`,`Habitat Stability Class`,`UCA Class`) %&gt;% 
                  summarise(
                    across(
                      c(pred.fit, pred.se.fit, pred_low, pred_high, pred_low_trend, pred_high_trend, value),
                      ~median(.x, na.rm=T)
                    ),
                    .groups = "drop"
                  ) %&gt;% 
                  mutate(`UCA Class`=as.character(`UCA Class`))
    ),
    Pred2 = map2(Pred2,rand_ef,
                 function(x,y){
                   x %&gt;% 
                     left_join(
                       y %&gt;% 
                         filter(term == "Year_n") %&gt;% 
                         filter(group_var== "`UCA Class`:`Stress Class`:`Habitat Stability Class`:SiteCode") %&gt;% 
                         filter(component == "cond") %&gt;% 
                         select(-group_var,-term,-sd,-`1`) %&gt;% 
                         rename(lower_slope_sig = pred_low,
                                upper_slope_sig = pred_high,
                                lower_slope_trend = pred_low_trend,
                                upper_slope_trend = pred_high_trend,
                                mean_slope = value,
                         )
                     )
                 }),
    Pred2 = map(Pred2,
                ~.x %&gt;% 
                  mutate(
                    linear_trend = case_when(
                      lower_slope_sig &gt; 0 ~ "Increasing Significant",
                      upper_slope_sig &lt; 0 ~ "Decreasing Significant",
                      lower_slope_trend &gt; 0 ~ "Increasing Trend",
                      upper_slope_trend &lt; 0 ~ "Decreasing Trend",
                      T ~ "No Change"
                    )
                  ) %&gt;% 
                  mutate(
                    linear_trend = factor(linear_trend,
                                          levels = c(
                                            "Increasing Significant",
                                            "Increasing Trend",
                                            "No Change",
                                            "Decreasing Trend",
                                            "Decreasing Significant"
                                          ))
                  )

    )
  )


