                                     Sample_Season=="Summer"))) %&gt;% 
  select(-Pred2) %&gt;% 
  unnest(Pred_current) %&gt;% 
  select(SiteCode,Endpoint,linear_trend ) %&gt;% 
  mutate(linear_trend = case_when(
    linear_trend == "Increasing Significant" ~ 2,
    linear_trend == "Increasing Trend" ~ 1,
    linear_trend == "No Change" ~ 0,
    linear_trend == "Decreasing Trend" ~ -1,
    linear_trend == "Decreasing Significant" ~ -2,
  )) %&gt;% 
  pivot_wider(values_from = linear_trend ,names_from = Endpoint) %&gt;% 
  select(-SiteCode)

t1 &lt;- cor(trend_cor,method = "spearman",use="pairwise.complete.obs")
keep &lt;- (!apply(t1,2,function(x) all(is.na(x))))
trend_cor &lt;- trend_cor[,keep]

trend_cor&lt;-heatmap_fn(
  trend_cor,
  alpha=0.1,
  sig_est=0.6,
  method="spearman",
  use="pairwise.complete.obs",
  scale_grad=ggplot2::scale_fill_gradient2(low = scales::muted(rgb(red=0/255,green=102/255,blue=255/255)),
                                           mid = "white",
                                           high = scales::muted(rgb(red=0/255,green=176/255,blue=80/255)),
                                           limits=c(-1,1))) 

cor_list$All_temp &lt;- trend_cor

saveRDS(cor_list,"data/cor_plots.rds")




