                                             ymax = pred_high,
                         ),linewidth = 0.25) +
                         geom_pointrange(aes(ymin = pred_low_trend,
                                             ymax = pred_high_trend,
                         ),linewidth = 1.5) +
                         geom_point(aes(x = gp,
                                        y = value),
                                    shape=21,size=2.5,inherit.aes = F) +
                         scale_colour_discrete() +
                         facet_grid(~group_var,scales="free", drop=TRUE) +
                         theme_bw() +
