  gt_tbl2 &lt;- gt_tbl2 %&gt;% gt::tab_spanner(label = i,columns  = as.character(ep_grp[[i]]))
}


# Trend Summary Table 3 ---------------------------------------------------

tbl_out &lt;- plot_out %&gt;% 
  select(Endpoint_group,Endpoint,summary_terms_temporal) %&gt;% 
  unnest(summary_terms_temporal) %&gt;% 
  filter(group_var == "`UCA Class`:`Habitat Stability Class`") %&gt;% 
  select(Endpoint_group,Endpoint,`Habitat Stability Class`,`UCA Class`,linear_trend) %&gt;% 
  filter(!is.na(`Habitat Stability Class`)) %&gt;% 
  mutate(`UCA Class` = paste("UCA",`UCA Class`)) %&gt;% 
  mutate(`UCA Class` = factor(`UCA Class`,levels=c("UCA Small","UCA Intermediate","UCA Large"))) %&gt;% 
  arrange(`UCA Class`)


ep_grp &lt;- tbl_out %&gt;% 
  select(Endpoint_group,Endpoint) %&gt;% 
  distinct()%&gt;% 
  group_by(Endpoint_group) %&gt;% 
  group_split() %&gt;% 
  map(~setNames(list(.x$Endpoint),.x$Endpoint_group[[1]])) %&gt;% 
  unlist(recursive = F)

site_grp &lt;- tbl_out %&gt;% 
  select(`Habitat Stability Class`,`UCA Class`) %&gt;% 
  distinct() %&gt;% 
  group_by(`Habitat Stability Class`) %&gt;% 
  group_split() %&gt;% 
  map(~setNames(list(.x$`UCA Class`),.x$`Habitat Stability Class`[[1]])) %&gt;% 
  unlist(recursive = F)

shade_tbl &lt;- tbl_out %&gt;% 
  select(-Endpoint_group) %&gt;% 
  pivot_wider(names_from = Endpoint,values_from = linear_trend) %&gt;% 
  arrange(`Habitat Stability Class`,`UCA Class`) %&gt;% 
  mutate(`UCA Class` = as.character(`UCA Class`)) %&gt;% 
  mutate(across(everything(),~case_when(is.na(.x) ~ "No Change", T ~ .x)))

site_grp &lt;- shade_tbl %&gt;% 
  mutate(`UCA Class` = row_number()) %&gt;% 
  select(`Habitat Stability Class`,`UCA Class`) %&gt;% 
  distinct() %&gt;% 
  group_by(`Habitat Stability Class`) %&gt;% 
  group_split() %&gt;% 
  map(~setNames(list(.x$`UCA Class`),.x$`Habitat Stability Class`[[1]])) %&gt;% 
  unlist(recursive = F)

site_grp &lt;- site_grp[c("Low","High")]
names(site_grp) &lt;- c(c("Low Stability","High Stability"))

writexl::write_xlsx(shade_tbl,"data/changetbl3.xlsx")

shade_tbl &lt;- shade_tbl %&gt;% 
  select(-`Habitat Stability Class`)

shade_tbl_sym &lt;- shade_tbl %&gt;% 
  mutate(across(everything(),
                ~case_when(
                  .x == "Increasing Significant" ~ "++",
                  .x == "Increasing Trend" ~ "+",
                  .x == "No Change" ~ "0",
                  .x == "Decreasing Trend" ~ "-",
                  .x == "Decreasing Significant" ~ "--",
                  is.na(.x) ~ "0",
                  T ~ .x
                )))

gt_tbl3 &lt;- gt::gt(shade_tbl_sym,rowname_col = "UCA Class") %&gt;% 
  gt::tab_stubhead(label = "UCA Class / Habitat Stability Class") %&gt;% 
  gt::tab_source_note(
    source_note = gt::md(col_list_leg)
  ) %&gt;% 
  gt::tab_style(
    style = gt::cell_text(align = "center", v_align = "middle"),
    locations = gt::cells_body(
      columns = everything(),
      rows = everything()
    )
  ) %&gt;% 
  gt::tab_style(
    style = gt::cell_text(align = "center",
                          v_align = "middle",
                          stretch = "extra-condensed",
                          size = "small"),
    locations = gt::cells_column_labels()
  ) %&gt;% 
  gt::tab_style(
    style = gt::cell_text(align = "center",
                          v_align = "middle",
                          stretch = "extra-condensed",
                          size = "small",
                          transform="capitalize"),
    locations = gt::cells_row_groups()
  )

for (i in names(col_list)){
  for (ii in 1:ncol(shade_tbl)){
    gt_tbl3 &lt;- gt_tbl3 %&gt;% 
      gt::tab_style(
        style = gt::cell_fill(color = col_list[[i]]),
        locations = gt::cells_body(
          columns = ii,
          rows = which(shade_tbl[[ii]] == i)
        )
      )
  }
}

for (i in names(site_grp)){
  gt_tbl3 &lt;- gt_tbl3 %&gt;% gt::tab_row_group(label = i,rows = site_grp[[i]])
}

for (i in names(ep_grp)){
  gt_tbl3 &lt;- gt_tbl3 %&gt;% gt::tab_spanner(label = i,columns  = as.character(ep_grp[[i]]))
}

gt_tbl4 &lt;- gt::gt_group(
  gt_tbl1,
  gt_tbl2,
  gt_tbl3
)

saveRDS(gt_tbl4,"data/summarytbl2.rds")




