mod_data_fin &lt;- readRDS("data/results_out.rds")

fish_ep &lt;- read_csv("data/fish_endpoints.csv") %&gt;% 
  select(SiteCode,Year,Endpoint,Value) %&gt;% 
  pivot_wider(names_from = Endpoint, values_from = Value) %&gt;% 
  select(SiteCode,Year, any_of(sel_fish_ep))

cor_list &lt;- list()
fish_plot_list &lt;- list()

# TRCA Data ---------------------------------------------------------------

cmb_data_TRCA &lt;- mod_data_fin %&gt;% 
  filter(TRCA) %&gt;% 
  select(Endpoint, data) %&gt;% 
  unnest(data) %&gt;% 
  pivot_wider(names_from = Endpoint,values_from = value) %&gt;% 
  left_join(fish_ep,by = join_by(SiteCode, Year))

hab_data_TRCA &lt;- cmb_data_TRCA %&gt;% 
  select(any_of(unlist(sapply(met_cols,names),use.names=F))#,
         # any_of(c("Upstream Catchment Area","Reach Slope",
         #          "Upstream catchment stress index","Upstream catchment developed (%)","Upstream catchment agricultural (%)"))
  )

fish_data_TRCA &lt;- cmb_data_TRCA %&gt;% 
  select(any_of(names(sel_fish_ep)))

cor_list$TRCA &lt;- heatmap_fn(hab_data_TRCA,
                            fish_data_TRCA,
                            alpha=0.1,
                            sig_est=0.6,
                            method="spearman",
                            scale_grad=ggplot2::scale_fill_gradient2(low = scales::muted(rgb(red=0/255,green=102/255,blue=255/255)),
                                                                     mid = "white",
                                                                     high = scales::muted(rgb(red=0/255,green=176/255,blue=80/255)),
                                                                     limits=c(-1,1))) 

fish_plot_list$TRCA &lt;- cor(fish_data_TRCA,hab_data_TRCA,method="spearman",use="pairwise.complete.obs") %&gt;% 
  as.data.frame() %&gt;% 
  rownames_to_column("var1") %&gt;% 
  pivot_longer(c(everything(),-var1),names_to = "var2") %&gt;% 
  mutate(abs_cor = abs(value)) %&gt;% 
  filter(abs_cor&gt;0.3)%&gt;% 
  arrange(desc(abs_cor)) %&gt;% 
  mutate(plot = map2(
    var1,
    var2,
    ~tibble(
      SiteCode = cmb_data_TRCA$StreamCode,
      SampleDate = cmb_data_TRCA$SampleDate,
      var2 = cmb_data_TRCA[[.y]],
      var1 = fish_data_TRCA[[.x]]
    ) %&gt;% 
      ggplot(aes(x=var2,y=var1))+
      geom_point()+
      geom_smooth(se=F)+
      ylab(.x)+
      xlab(.y)+
      theme_bw()

  )) %&gt;% 
  mutate(plot = map2(plot,value,
                     ~.x + labs(
                       #title = "TRCA Data Only",
                       subtitle = paste0("Spearman Correlation = ",scales::label_number(0.001)(.y)))
  ))

# All Data ----------------------------------------------------------------

cmb_data_all &lt;- mod_data_fin %&gt;% 
  filter(!TRCA) %&gt;% 
  select(Endpoint, data) %&gt;% 
  unnest(data) %&gt;% 
  pivot_wider(names_from = Endpoint,values_from = value) %&gt;% 
  left_join(fish_ep,by = join_by(SiteCode, Year))

hab_data_all &lt;- cmb_data_all %&gt;% 
  select(any_of(unlist(sapply(met_cols,names),use.names=F)),
         any_of(c("Upstream Catchment Area","Reach Slope",
                  "Upstream catchment stress index","Upstream catchment developed (%)","Upstream catchment agricultural (%)"))
  )

fish_data_all &lt;- cmb_data_all %&gt;% 
  select(any_of(names(sel_fish_ep)))

